import React from 'react';

class App extends React.Component {

  state = { resource: 'posts' };

  render() {
    return (
      <div>

        <div>
          <button onClick={() => this.setState('posts')}>posts</button>
          <button onClick={() => this.setState('todos')}>todos</button>

          {this.state.resource}

        </div>

      </div>
    );
  }
}

export default App;
